begin;

create schema forum_example;
create schema forum_example_private;

create table forum_example.person ( id               serial primary key, username         text unique, first_name       text not null check (char_length(first_name) < 80), last_name        text check (char_length(last_name) < 80), about            text, created_at       timestamp default now());
comment on table forum_example.person is 'A user of the forum.';
comment on column forum_example.person.id is 'The primary unique identifier for the person.';
comment on column forum_example.person.id is '@omit update';
comment on column forum_example.person.first_name is 'The person''s first name.';
comment on column forum_example.person.last_name is 'The person''s last name.';
comment on column forum_example.person.about is 'A short description about the user, written by the user.';
comment on column forum_example.person.created_at is 'The time this person was created.';

create type forum_example.post_topic as enum ( 'discussion', 'inspiration', 'help', 'showcase');

create table forum_example.post ( id               serial primary key, author_username  text not null references forum_example.person(username), headline         text not null check (char_length(headline) < 280), body             text, topic            forum_example.post_topic, created_at       timestamp default now());
comment on constraint "post_author_username_fkey" on forum_example.post is E'@foreignFieldName posts\n@fieldName author';
comment on table forum_example.post is 'A forum post written by a user.';
comment on column forum_example.post.id is 'The primary key for the post.';
comment on column forum_example.post.headline is 'The title written by the user.';
comment on column forum_example.post.author_username is 'The id of the author user.';
comment on column forum_example.post.topic is 'The topic this has been posted in.';
comment on column forum_example.post.body is 'The main body text of our post.';
comment on column forum_example.post.created_at is 'The time this post was created.';

alter default privileges revoke execute on functions from public;

create function forum_example.person_full_name(person forum_example.person) returns text as $$ select person.first_name || ' ' || person.last_name $$ language sql stable;
comment on function forum_example.person_full_name(forum_example.person) is 'A person''s full name which is a concatenation of their first and last name.';

create function forum_example.post_summary( post forum_example.post, length int default 50, omission text default 'â€¦') returns text as $$ select case when post.body is null then null else substr(post.body, 0, length) || omission end $$ language sql stable;

comment on function forum_example.post_summary(forum_example.post, int, text) is 'A truncated version of the body for summaries.';

create function forum_example.person_latest_post(person forum_example.person) returns forum_example.post as $$ select post.* from forum_example.post as post where post.author_username = person.username order by created_at desc limit 1 $$ language sql stable;

comment on function forum_example.person_latest_post(forum_example.person) is 'Gets the latest post written by the person.';

create function forum_example.search_posts(search text) returns setof forum_example.post as $$ select post.* from forum_example.post as post where post.headline ilike ('%' || search || '%') or post.body ilike ('%' || search || '%') $$ language sql stable;
comment on function forum_example.search_posts(text) is 'Returns posts containing a given search term.';

alter table forum_example.person add column updated_at timestamp default now();
alter table forum_example.post add column updated_at timestamp default now();

create function forum_example_private.set_updated_at() returns trigger as $$ begin new.updated_at := current_timestamp; return new; end; $$ language plpgsql;

create trigger person_updated_at before update on forum_example.person for each row execute procedure forum_example_private.set_updated_at();
create trigger post_updated_at before update on forum_example.post for each row execute procedure forum_example_private.set_updated_at();
create function forum_example.register_person( first_name text, last_name text, username text) returns forum_example.person as $$ declare person forum_example.person; begin insert into forum_example.person (first_name, last_name, username) values (first_name, last_name, username) returning * into person; return person; end; $$ language plpgsql strict security definer;
comment on function forum_example.register_person(text, text, text) is 'Registers a single user and creates an account in our forum.';

create role lambda_runner login password '<<<lambda_runner_password>>>';

create role forum_example_anonymous;
grant forum_example_anonymous to lambda_runner;

create role forum_example_person;
grant forum_example_person to lambda_runner;

create function forum_example.current_person() returns forum_example.person as $$ select * from forum_example.person where username = current_setting('identity.username')::text $$ language sql stable;
comment on function forum_example.current_person() is 'Gets the person who was identified by our cognito identity.';

grant usage on schema forum_example to forum_example_anonymous, forum_example_person;

grant select on table forum_example.person to forum_example_anonymous, forum_example_person;
grant update, delete on table forum_example.person to forum_example_person;

grant select on table forum_example.post to forum_example_anonymous, forum_example_person;
grant insert, update, delete on table forum_example.post to forum_example_person;
grant usage on sequence forum_example.post_id_seq to forum_example_person;

grant execute on function forum_example.person_full_name(forum_example.person) to forum_example_anonymous, forum_example_person;
grant execute on function forum_example.post_summary(forum_example.post, integer, text) to forum_example_anonymous, forum_example_person;
grant execute on function forum_example.person_latest_post(forum_example.person) to forum_example_anonymous, forum_example_person;
grant execute on function forum_example.search_posts(text) to forum_example_anonymous, forum_example_person;
grant execute on function forum_example.current_person() to forum_example_anonymous, forum_example_person;

grant execute on function forum_example.register_person(text, text, text) to forum_example_anonymous;

alter table forum_example.person enable row level security;
alter table forum_example.post enable row level security;

create policy select_person on forum_example.person for select using (true);

create policy select_post on forum_example.post for select using (true);

create policy update_person on forum_example.person for update to forum_example_person using (username = current_setting('identity.username')::text);

create policy delete_person on forum_example.person for delete to forum_example_person using (username = current_setting('identity.username')::text);

create policy insert_post on forum_example.post for insert to forum_example_person with check (author_username = current_setting('identity.username')::text);

create policy update_post on forum_example.post for update to forum_example_person using (author_username = current_setting('identity.username')::text);

create policy delete_post on forum_example.post for delete to forum_example_person using (author_username = current_setting('identity.username')::text);

commit;


